import { Component } from '@angular/core';
import {PopularsMoviesService} from './services/populars-movies.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'MovieDB';

  arrMoviePopular: any[] = [];

  currentPage: number;
  numPages: number;

  constructor(private popularService: PopularsMoviesService){
    this.currentPage = 1;
    this.numPages = 0;
  }

  ngOnInit(){
     this.popularService.getAll()
     .then(res => {
      this.arrMoviePopular = res['results'];
      this.numPages = res['total_pages'];
      console.log(this.arrMoviePopular)
     })
  }

  getPopular(){
    this.popularService.getAll()
     .then(res => {
      this.arrMoviePopular = res['results'];
      console.log(this.arrMoviePopular)
     })
  }

  async changePage(siguiente: boolean){
    if (siguiente){ this.currentPage++}
    else {this.currentPage--}

    const resultsChange = await this.popularService.getAll(this.currentPage)
    this.arrMoviePopular = resultsChange['results'];
  }
}
<app-banner></app-banner>

<div class="moviesPopular">
  <div class="moviePop" *ngFor="let movie of arrMoviePopular">
    <img src="https://image.tmdb.org/t/p/w500/{{movie.poster_path}}" alt="movie">
    <h2>{{ movie.original_title }}</h2>
    <p>{{ movie.overview }}</p>
    <p class="dataMovie">🗓 {{movie.release_date}}</p>
  </div>
</div>

<div class="next">
  <button (click)="changePage(false)" [disabled]="currentPage === 1"> Anterior </button>
  <button (click)="changePage(true)" [disabled]="currentPage === numPages">Siguiente</button>
</div>
